import React from 'react';
import './App.css';
import {Collapse} from 'react-collapse';

let searchTerm;
class App extends React.Component {
  constructor(props) {
    super(props);
    this.onClick = this.onClick.bind(this);
    this.state = { 
      activeIndex: null,
      repositories: [] };
  }

  render() {
    return (
      <div className="container">
        <h2>Repositories
      <form>
            <input type="text" className="searchbox" ref={(input) => { this.searchBox = input; }} />
            <button onClick={this.onClick}>Search</button>
          </form>
        </h2>
        <ul>
          {this.state.repositories.map((item, index) => (
            <li key={index} className="months">
              <Collapse isOpened={activeIndex === index}>
                <h3>
                  {item.name}
                </h3>
                <ul>
                  <li>
                    <h4>URL: <p>{item.owner.url}</p></h4>
                  </li>
                  <li>
                    <h4>Owner: <p>{item.owner.login}</p></h4>
                  </li>
                  <li>
                    <h4>Description:</h4>
                    <p>{item.description}</p>
                  </li>
                  <li>
                    <h4>Open Issues: <p>{item.open_issues_count}</p></h4>
                  </li>
                  <li>
                    <h4>Number of watchers: <p>{item.watchers}</p></h4>
                  </li>
                  <li>
                    <h4>Last Push: <p>{item.pushed_at}</p></h4>
                  </li>
                  <li>
                    <h4><a href={'https://api.github.com/search/issues?q=repo:' + item.full_name}>Issues</a></h4>
                  </li>
                </ul>
              </Collapse>
            </li>
          ))}
        </ul>
      </div>
    );
  }

  onClick(event) {
    searchTerm = this.searchBox.value;
    let endpoint = 'https://api.github.com/search/repositories?&q=' + searchTerm;
    console.log(searchTerm);
    fetch(endpoint)
      .then(blob => blob.json())
      .then(response => {
        this.setState({ repositories: response.items });
      });
    event.preventDefault();
  }



}

export default App;
