import React from 'react';
import './App.css';

let searchTerm;

class App extends React.Component {

  constructor(props) {
    super(props);
    this.onClick = this.onClick.bind(this);
    this.state = { repositories: [], issues: [] };
  }


  render() {
    return (
      <div>
        <form>
          <input type="text" className="searchbox" ref={(input) => { this.searchBox = input; }} />
          <button onClick={this.onClick}>Search</button>
        </form>
        <div className="foundRepo">{this.props.name}</div>
        <h2>Repositories</h2>
        <ul>
          {this.state.repositories.map((item, index) => (
            <li key={index}>
              <h3>
                {item.name}
              </h3>
              <ul>
                <li>
                  <h4>URL: <p>{item.owner.url}</p></h4>
                </li>
                <li>
                  <h4>Owner: <p>{item.owner.login}</p></h4>

                </li>
                <li>
                  <h4>Description:<p>{item.description}</p></h4>
                </li>
                <li>
                  <h4>
                    Open Issues:
                </h4>
                  {item.open_issues_count}
                </li>
                <li>
                  <h4>
                    Number of watchers:
                </h4>
                  {item.watchers}
                </li>
                <li>
                  <h4>
                    Last Push:
                </h4>
                  {item.pushed_at}
                </li>
                <li>
                  Issues: {'https://api.github.com/repos/' + item.full_name + '/issues'}
                </li>
              </ul>
            </li>
          ))}
        </ul>
      </div>
    );
  }

  onClick(event) {

    searchTerm = this.searchBox.value;
    let endpoint = 'https://api.github.com/search/repositories?&q=' + searchTerm;
    console.log(searchTerm);
    fetch(endpoint)
      .then(blob => blob.json())
      .then(response => {
        this.setState({ repositories: response.items });
      });
    event.preventDefault();

  }
}

export default App;
