import React from 'react';
import './App.css';

let searchTerm;

class App extends React.Component {

  constructor(props) {
    super(props);
    this.onClick = this.onClick.bind(this);
    this.state = { repositories: [] };
  }


  render() {
    return (
      <div>
        <form>
          <input type="text" className="searchbox" ref={(input) => { this.searchBox = input; }} />
          <button onClick={this.onClick}>Search</button>
        </form>
        <div className="foundRepo">{this.props.name}</div>
        <h2>Repositories</h2>
        <ul>
          {this.state.repositories.map((item, index) => (
            <li key={index}>
              <li>
                {item.name}
              </li>
              <ul>
                <li>
                 Owner: {item.owner.login}
                </li>
                <li>
                 Description: {item.description}
                </li>
                <li>
                  Open Issues: {item.open_issues_count}
                </li>
                <li>
                  Number of watchers: {item.watchers}
                </li>
                <li>
                  Last Push: {item.pushed_at}
                </li>
              </ul>
            </li>
          ))}
        </ul>
      </div>
    );
  }

  onClick(event) {

    searchTerm = this.searchBox.value;
    let endpoint = 'https://api.github.com/search/repositories?&q=' + searchTerm;
    console.log(searchTerm);
    fetch(endpoint)
      .then(blob => blob.json())
      .then(response => {
        this.setState({ repositories: response.items });
      });
    event.preventDefault();

  }
}

export default App;
